    const handleSubmit = async (e) => {
        e.preventDefault();
        await addTask(taskName);
        setTaskName("");
        onTaskAdded();
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Nouvelle tÃ¢che"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
            />
            <button type="submit">Ajouter</button>
        </form>
    );
};

export default TaskForm;
